    sqlite3_prepare_v2(database, sql, -1, &prepared_stmt, NULL);
    sqlite3_bind_text(prepared_stmt, 1, username, -1, SQLITE_TRANSIENT);
    sqlite3_bind_int(prepared_stmt, 2, age);
    sqlite3_step(prepared_stmt);
    sqlite3_finalize(prepared_stmt);
