#include <libssh2_sftp.h>

void replicate_data_via_sftp(LIBSSH2_SFTP *sftp, const char *local_file, const char *remote_dest) {
    FILE *src = fopen(local_file, "rb");
    LIBSSH2_SFTP_HANDLE *dst = libssh2_sftp_open(sftp, remote_dest, O_CREAT | O_WRONLY | O_TRUNC, 0644);
    unsigned char buffer[4096];
    size_t bytes;
    while ((bytes = fread(buffer, 1, sizeof(buffer), src)) > 0) {
        libssh2_sftp_write(dst, (char *)buffer, bytes);
    }
    fclose(src);
    libssh2_sftp_close_handle(dst);
}

