    LIBSSH2_SFTP_HANDLE *remote_handle = libssh2_sftp_open(sftp_session, remote_location, O_WRONLY | O_CREAT | O_EXCL, 0755);
    FILE *artifact = fopen(artifact_path, "rb");
    char buffer[8192];
    size_t read_bytes;
    while ((read_bytes = fread(buffer, 1, sizeof(buffer), artifact)) > 0) {
        libssh2_sftp_write(remote_handle, buffer, read_bytes);
