    const char *soap_body = "<?xml version=\"1.0\"?><soap:Envelope xmlns:soap=\"http://schemas.xmlsoap.org/soap/envelope/\"><soap:Body></soap:Body></soap:Envelope>";
    curl_easy_setopt(curl, CURLOPT_URL, endpoint);
