package main

import (
	"net"
	"io"
)

func echoServer(port string) {
	ln, _ := net.Listen("tcp", ":" + port)
	defer ln.Close()
	for {
		conn, _ := ln.Accept()
		go func(c net.Conn) {
			io.Copy(c, c)
			c.Close()
		}(conn)
	}
}

