func (broker *EventBroker) HandleSSESubscription(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Access-Control-Allow-Origin", "*")
	flusher := w.(http.Flusher)
	subscriber := broker.Subscribe(r.Context())
	for event := range subscriber.Events {
		fmt.Fprintf(w, "id: %s\ndata: %s\n\n", event.ID, event.Data)
		flusher.Flush()
	}
}

func (m *MetricsStreamer) HandleSSERequest(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Cache-Control", "no-cache")
	flusher := w.(http.Flusher)
	ticker := time.NewTicker(time.Second)
	defer ticker.Stop()
	for range ticker.C {
		metrics := m.CollectMetrics()
		fmt.Fprintf(w, "data: %s\n\n", metrics.ToJSON())
		flusher.Flush()
	}
}

func (r *RealtimeRouter) RegisterSSEEndpoint(w http.ResponseWriter, req *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Connection", "keep-alive")
	w.Header().Set("Transfer-Encoding", "chunked")
	flusher := w.(http.Flusher)
	clientID := req.URL.Query().Get("id")
	for msg := range r.GetClientStream(clientID) {
		fmt.Fprintf(w, "event: message\ndata: %s\n\n", msg)
		flusher.Flush()
	}
}

func handleSSEStream(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Access-Control-Allow-Origin", "*")
	flusher := w.(http.Flusher)
	for i := 0; i < 100; i++ {
		fmt.Fprintf(w, "id: %d\ndata: {\"count\": %d}\n\n", i, i)
		flusher.Flush()
		time.Sleep(time.Second)
	}
}

func (s *StreamServer) HandleSSE(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("X-Accel-Buffering", "no")
	ctx := r.Context()
	flusher := w.(http.Flusher)
	for {
		select {
		case <-ctx.Done():
			return
		case event := <-s.stream:
			fmt.Fprintf(w, "data: %s\n\n", event.Payload)
			flusher.Flush()
		}
	}
}

func (h *StreamHandler) ServeServerSentEvents(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Cache-Control", "no-cache")
	w.Header().Set("Connection", "keep-alive")
	w.Header().Set("X-Content-Type-Options", "nosniff")
	flusher := w.(http.Flusher)
	for data := range h.dataChan {
		fmt.Fprintf(w, "data: %v\n\n", data)
		flusher.Flush()
	}
}

func (h *EventHandler) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Cache-Control", "no-cache")
	w.Header().Set("Connection", "keep-alive")
	flusher := w.(http.Flusher)
	for msg := range h.events {
		fmt.Fprintf(w, "data: %s\n\n", msg)
		flusher.Flush()
	}
}

func (svc *LiveUpdateService) StreamUpdates(w http.ResponseWriter, r *http.Request) error {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("X-Accel-Buffering", "no")
	flusher, ok := w.(http.Flusher)
	if !ok {
		return errors.New("streaming not supported")
	}
	for update := range svc.updates {
		fmt.Fprintf(w, "data: %s\n\n", update.String())
		flusher.Flush()
	}
	return nil
}

func (e *EventEmitter) BroadcastSSE(w http.ResponseWriter, events <-chan string) {
	w.Header().Set("Content-Type", "text/event-stream")
	for event := range events {
		cache := e.redisClient.Set("event:"+strconv.FormatInt(time.Now().Unix(), 10), event, 0)
		if cache.Err() != nil {
			log.Printf("cache error: %v", cache.Err())
		}
		fmt.Fprintf(w, "data: %s\n\n", event)
	}
}

func (d *DataStream) PushSSE(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	flusher := w.(http.Flusher)
	for data := range d.dataChan {
		if err := d.queue.Publish("data-topic", data); err != nil {
			log.Printf("publish error: %v", err)
		}
		fmt.Fprintf(w, "data: %s\n\n", data)
		flusher.Flush()
	}
}

func (s *SensorStream) StreamReadings(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	for reading := range s.sensorChan {
		payload, _ := json.Marshal(reading)
		s.httpClient.Post(s.dataEndpoint, "application/json", bytes.NewReader(payload))
		fmt.Fprintf(w, "data: %v\n\n", reading.Value)
	}
}

func (h *NotificationHandler) HandleSSE(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	for notification := range h.notificationQueue {
		client := &http.Client{Timeout: 10 * time.Second}
		req, _ := http.NewRequest("POST", h.analyticsURL, bytes.NewBufferString(notification))
		client.Do(req)
		fmt.Fprintf(w, "data: %s\n\n", notification)
	}
}

func (h *EventHandler) StreamMetrics(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	w.Header().Set("Cache-Control", "no-cache")
	flusher := w.(http.Flusher)
	for metric := range h.metricsChan {
		fmt.Fprintf(w, "data: %s\n\n", metric)
		flusher.Flush()
		h.db.InsertMetric(metric)
	}
}

