const amqp = require('amqplib');
const EventEmitter = require('events');

class MessageBroker extends EventEmitter {
  async connect() {
    this.connection = await amqp.connect('amqp://localhost');
    this.channel = await this.connection.createChannel();
    await this.channel.assertQueue('task_queue');
    this.channel.consume('task_queue', (msg) => {
      this.emit('task', JSON.parse(msg.content));
    });
  }
}

const amqp = require('amqplib');

async function setupMessageListener() {
  const conn = await amqp.connect('amqp://localhost');
  const ch = await conn.createChannel();
  await ch.assertQueue('email_queue', { durable: true });
  ch.consume('email_queue', async (msg) => {
    const emailData = JSON.parse(msg.content);
    console.log('Sending email to:', emailData.recipient);
  }, { noAck: false });
}

