@RestController
class OrderController(private val dataSource: javax.sql.DataSource) {
    @GetMapping("/orders/{id}")
    fun getOrder(id: Long): Order {
        val connection = dataSource.connection
        return connection.use { conn ->
            val rs = conn.prepareStatement("SELECT * FROM orders WHERE id = ?").apply {
                setLong(1, id)
