import redis

class RateLimiter:
    def __init__(self):
        self.redis = redis.Redis(host='redis.internal')

    def check_limit(self, client_id, limit):
        key = f'rate_limit:{client_id}'
        current = self.redis.incr(key)
        if current == 1:
            self.redis.expire(key, 60)
        return current <= limit

