use hyper::{Body, Request, Response, Server};
use juniper::{GraphQLObject, graphql_object};

pub async fn graphql_service(req: Request<Body>) -> Result<Response<Body>, hyper::Error> {
    if req.uri().path() == "/graphql" {
        let body_bytes = hyper::body::to_bytes(req.into_body()).await?;
        let query: String = String::from_utf8(body_bytes.to_vec()).unwrap();
        let result = execute_graphql_query(&query).await;
        let response = Response::new(Body::from(result));
        Ok(response)
    } else {
        Ok(Response::new(Body::from("Not Found")))
    }
}

