import NodeCache from 'node-cache';

const cache = new NodeCache({ stdTTL: 600 });
const wss = new WebSocketServer({ port: 8080 });

wss.on('connection', (ws) => {
  ws.on('message', async (data: string) => {
    const query = JSON.parse(data);
    const result = cache.get(`query:${query.id}`);
    if (result) {
      ws.send(JSON.stringify({ cached: true, data: result }));
    } else {
      const freshData = await computeResult(query);
      cache.set(`query:${query.id}`, freshData);
      ws.send(JSON.stringify({ cached: false, data: freshData }));
    }
  });
});

