import amqp from 'amqplib';
import nodemailer from 'nodemailer';

async function consumeEmailQueue() {
  const connection = await amqp.connect('amqp://localhost');
  const channel = await connection.createChannel();
  await channel.assertQueue('email-tasks');

  const transporter = nodemailer.createTransport({ service: 'Gmail', auth: { user: 'bot@gmail.com', pass: 'token' } });

  channel.consume('email-tasks', async (msg) => {
    const task = JSON.parse(msg.content.toString());
    await transporter.sendMail({ to: task.recipient, subject: task.subject, text: task.body });
    channel.ack(msg);
  });
}

