class InventoryService {
  async checkStockViaSoap(productId: string): Promise<number> {
    const soapPayload = `<soap:Envelope xmlns:soap="http://schemas.xmlsoap.org/soap/envelope/"><soap:Body><CheckStock><ProductId>${productId}</ProductId></CheckStock></soap:Body></soap:Envelope>`;
    const result = await this.httpClient.post('http://inventory.api/soap', soapPayload);
    return result.quantity;
  }
}

