public class ReportExporter
{
    public void SendReportToFtp(byte[] reportData, string reportName)
    {
        var ftpUrl = $"ftp://reports.company.com/monthly/{reportName}";
        var request = (FtpWebRequest)WebRequest.Create(ftpUrl);
        request.Method = WebRequestMethods.Ftp.UploadFile;
        request.UseBinary = true;

        using (var stream = request.GetRequestStream())
        {
            stream.Write(reportData, 0, reportData.Length);
        }
    }
}

public class DocumentSynchronizer
{
    private string _ftpServer = "ftp://sync.documents.com";

    public void SyncDocument(string documentPath)
    {
        var ftpRequest = (FtpWebRequest)WebRequest.Create(_ftpServer + "/sync/" + Path.GetFileName(documentPath));
        ftpRequest.Method = WebRequestMethods.Ftp.UploadFile;
        ftpRequest.Credentials = GetCredentials();

        using (var fileStream = File.OpenRead(documentPath))
        using (var ftpStream = ftpRequest.GetRequestStream())
        {
            fileStream.CopyTo(ftpStream);
        }
    }
}

