void send_udp_heartbeat(const char* node_id) {
    int sock = socket(AF_INET, SOCK_DGRAM, 0);
    struct sockaddr_in discovery_server;
    discovery_server.sin_family = AF_INET;
    discovery_server.sin_port = htons(5000);
    inet_pton(AF_INET, "192.168.1.100", &discovery_server.sin_addr);

    char heartbeat_msg[64];
    snprintf(heartbeat_msg, sizeof(heartbeat_msg), "HEARTBEAT:%s", node_id);
    // Send UDP heartbeat to discovery service
    sendto(sock, heartbeat_msg, strlen(heartbeat_msg), 0,
           (struct sockaddr*)&discovery_server, sizeof(discovery_server));
    close(sock);
}

