import "github.com/confluentinc/confluent-kafka-go/kafka"

type EventProcessor struct {
	consumer *kafka.Consumer
}

func (p *EventProcessor) StartConsuming() {
	p.consumer.SubscribeTopics([]string{"user-events"}, nil)
	for {
		msg, err := p.consumer.ReadMessage(-1)
		if err == nil {
			p.handleUserEvent(msg)
		}
	}
}

func (s *PaymentService) HandleKafkaEvent(ctx context.Context) {
	reader := kafka.NewReader(kafka.ReaderConfig{
		Brokers: []string{"localhost:9092"},
		Topic:   "payments",
	})
	for {
		msg, err := reader.ReadMessage(ctx)
		if err == nil {
			s.processPayment(msg.Value)
		}
	}
}

func sendOrderNotification(orderID string, customerEmail string) {
    conn, err := amqp.Dial("amqp://guest:guest@localhost:5672/")
    if err != nil {
        log.Fatal(err)
    }
    defer conn.Close()

    ch, _ := conn.Channel()
    defer ch.Close()

    notification := OrderNotification{OrderID: orderID, Email: customerEmail}
    body, _ := json.Marshal(notification)

    ch.Publish("", "notifications", false, false, amqp.Publishing{
        ContentType: "application/json",
        Body:        body,
    })
}

func publishUserEvent(userID string, event UserEvent) error {
    producer := kafka.NewProducer(&kafka.ConfigMap{
        "bootstrap.servers": "localhost:9092",
    })
    defer producer.Close()

    message := &kafka.Message{
        TopicPartition: kafka.TopicPartition{Topic: &"user-events", Partition: kafka.PartitionAny},
        Value:          []byte(event.ToJSON()),
    }

    return producer.Produce(message, nil)
}

