@Service
class PaymentGrpcService : PaymentServiceGrpc.PaymentServiceImplBase() {

    override fun processPayment(request: PaymentRequest, responseObserver: StreamObserver<PaymentResponse>) {
        try {
            val result = paymentProcessor.charge(request.amount, request.cardToken)
            responseObserver.onNext(PaymentResponse.newBuilder().setSuccess(true).build())
        } catch (e: Exception) {
            logger.error("GRPC payment processing failed", e)
            responseObserver.onError(Status.INTERNAL.withDescription("Payment failed").asException())
        }
        responseObserver.onCompleted()
    }
}

