use lettre::transport::smtp::authentication::Credentials;
use lettre::{Message, SmtpTransport, Transport};

#[tokio::main]
async fn handle_email_webhook(payload: EmailWebhookPayload) -> Result<(), Box<dyn std::error::Error>> {
    let email = Message::builder()
        .from("noreply@company.com".parse()?)
        .to(payload.recipient.parse()?)
        .subject(&payload.subject)
        .body(payload.body)?;

    let creds = Credentials::new("smtp_user".to_owned(), "smtp_pass".to_owned());
    let mailer = SmtpTransport::relay("smtp.gmail.com")?
        .credentials(creds)
        .build();

    mailer.send(&email)?;
    Ok(())
}

