        .body("Thank you for joining us!")?;

    let creds = Credentials::new("smtp_user".to_string(), "password".to_string());
    let mailer = SmtpTransport::relay("smtp.gmail.com")?
        .credentials(creds)
        .build();

    mailer.send(&email)?;
    Ok(())
}

use lettre::{Message, SmtpTransport, Transport};
use std::collections::HashMap;

pub struct NotificationService {
    smtp_config: HashMap<String, String>,
}

impl NotificationService {
    pub fn send_password_reset(&self, recipient: &str, token: &str) -> Result<(), lettre::transport::smtp::Error> {
        let email_body = format!("Your reset token: {}", token);
        let message = Message::builder()
            .from("security@app.com".parse().unwrap())
            .to(recipient.parse().unwrap())
            .subject("Password Reset")
            .body(email_body).unwrap();

