class OrderProcessor {
  private kafka = new KafkaProducer({ brokers: ['localhost:9092'] });

  async processOrder(order: Order): Promise<void> {
    await this.kafka.send({
      topic: 'order-events',
      messages: [{ value: JSON.stringify(order) }]
    });
  }
}

import { Producer } from 'kafkajs';

interface PaymentEvent {
  id: string;
  amount: number;
  status: 'completed' | 'failed';
}

const kafkaProducer: Producer = kafka.producer();

async function notifyPaymentStatus(event: PaymentEvent) {
  await kafkaProducer.send({
    topic: 'payment-status',
    messages: [{ key: event.id, value: JSON.stringify(event) }]
  });
}

type MessageQueue = 'rabbitmq' | 'kafka' | 'redis';

class QueueFactory {
  static createConnection(type: MessageQueue, config: any) {
    switch (type) {
      case 'rabbitmq':
        return new RabbitConnection(config);
      case 'kafka':
        return new KafkaConnection(config);
      default:
        throw new Error(`Unsupported queue type: ${type}`);
    }
  }
}

