class ChatClient {
  private messageStream?: EventSource;

  joinRoom(roomId: string): void {
    this.messageStream = new EventSource(`/chat/rooms/${roomId}/sse`);
    this.messageStream.addEventListener('message', (event) => {
      const message = JSON.parse(event.data);
      this.displayMessage(message);
    });
  }

  leaveRoom(): void {
    this.messageStream?.close();
  }
}

export function establishServerSentEvents(channelId: string): EventSource {
  const endpoint = `${process.env.STREAMING_URL}/channels/${channelId}/events`;
  const eventStream = new EventSource(endpoint, {
    withCredentials: true
  });

  eventStream.onopen = () => console.log('Connected to server sent events');
  return eventStream;
}

