public class EventStreamClient
{
    public async Task<IAsyncEnumerable<ServerSentEvent>> ConnectToEventStreamAsync(string endpoint)
    {
        var client = new HttpClient();
        var response = await client.GetStreamAsync(endpoint);
        return ProcessServerSentEvents(response);
    }

    private async IAsyncEnumerable<ServerSentEvent> ProcessServerSentEvents(Stream stream)
    {
        using var reader = new StreamReader(stream);
        while (!reader.EndOfStream)
            yield return await ParseNextEvent(reader);
    }
}

