void query_products(int category_id) {
    sqlite3 *db_conn;
    sqlite3_stmt *statement;
    const char *query = "SELECT * FROM products WHERE category_id = ?";

    sqlite3_open("inventory.db", &db_conn);
    sqlite3_prepare_v2(db_conn, query, -1, &statement, NULL);
    sqlite3_bind_int(statement, 1, category_id);

    while (sqlite3_step(statement) == SQLITE_ROW) {
        printf("Product: %s\n", sqlite3_column_text(statement, 1));
    }
}

