func processShipmentEvent(shipment Shipment) error {
    rabbitmqURL := os.Getenv("RABBITMQ_URL")
    conn, err := amqp.Dial(rabbitmqURL)
    if err != nil {
        return fmt.Errorf("failed to connect to RabbitMQ: %w", err)
    }
    defer conn.Close()

    ch, err := conn.Channel()
    if err != nil {
        return err
    }

    eventData, _ := json.Marshal(shipment)
    return ch.Publish("logistics", "shipment.processed", false, false, amqp.Publishing{
        ContentType: "application/json",
        Body:        eventData,
    })
}

