pub struct NotificationHandler {
    email_queue: mpsc::Sender<EmailMessage>,
}

#[tonic::async_trait]
impl NotificationService for NotificationHandler {
    async fn send_notification(&self, req: Request<NotificationRequest>) -> Result<Response<NotificationAck>, Status> {
        let notification = req.into_inner();
        self.email_queue.send(EmailMessage::from(notification)).await.unwrap();
        Ok(Response::new(NotificationAck { success: true }))
    }
}

async fn send_notification(channel: tonic::transport::Channel) -> Result<(), Box<dyn std::error::Error>> {
