    let message = NotificationMessage {
        recipient: "user@example.com".to_string(),
        content: "Your order is ready".to_string(),
    };
    client.send_notification(tonic::Request::new(message)).await?;
    println!("Notification sent via gRPC");
    Ok(())
}

