class BackupScheduler(redisClient: RedisClient) {
  def initializeScheduler(): Unit = {
    val backupTask = new Runnable {
      def run(): Unit = {
        val data = fetchApplicationData()
        redisClient.set(s"backup:${System.currentTimeMillis()}", data)
      }
    }

    val scheduler = ScheduledExecutorService.newSingleThreadScheduledExecutor()
    scheduler.scheduleWithFixedDelay(backupTask, 0, 30, TimeUnit.MINUTES)
  }
}

