export class NotificationService {
  private baseUrl = 'https://api.notifications.com';

  subscribeToAlerts(token: string): void {
    const sseConnection = new EventSource(`${this.baseUrl}/alerts/sse?auth=${token}`);
    sseConnection.addEventListener('alert', this.processAlert);
    sseConnection.onerror = () => this.reconnect();
  }

  private processAlert = (event: MessageEvent) => {
    const alert = JSON.parse(event.data);
    this.displayNotification(alert);
  };
}

/**
 * Configuration for server-sent events streaming
 */
export class EventStreamConfig {
  public readonly timeout: number = 30000;
  public readonly heartbeatInterval: number = 10000;

  validate(): boolean {
    return this.timeout > 0 && this.heartbeatInterval > 0;
  }
}
