[ApiController]
[Route("api/documents")]
public class DocumentUploadController : ControllerBase
{
    [HttpPost("upload-file")]
    public async Task<IActionResult> UploadFile(IFormFile file, [FromForm] string category)
    {
        var fileName = $"{category}_{DateTime.UtcNow.Ticks}{Path.GetExtension(file.FileName)}";
        var fullPath = Path.Combine("documents", category, fileName);
        Directory.CreateDirectory(Path.GetDirectoryName(fullPath));
        using (var fileStream = new FileStream(fullPath, FileMode.Create))
        {
            await file.CopyToAsync(fileStream);
        }
        return CreatedAtAction(nameof(UploadFile), new { fileName });
    }
}

