public class NotificationHub : Hub
{
    public async IAsyncEnumerable<NotificationMessage> SubscribeToNotifications()
    {
        var queue = new Channel<NotificationMessage>();
        yield return new NotificationMessage { Id = 1, Text = "Connected" };
        await foreach (var msg in queue.Reader.ReadAllAsync())
        {
