public async IAsyncEnumerable<string> StreamWeatherUpdates(string city)
{
    var client = new HttpClient();
    while (true)
    {
        var response = await client.GetAsync($"https://api.weather.com/current?city={city}");
        var data = await response.Content.ReadAsStringAsync();
        yield return data;
        await Task.Delay(5000);
    }
}

