func (p *PricingAPI) GetPrice(w http.ResponseWriter, r *http.Request) {
	productID := r.URL.Query().Get("product")
	if price, ok := p.memcached.GetPrice(productID); ok {
		w.Header().Set("Content-Type", "application/json")
		json.NewEncoder(w).Encode(map[string]interface{}{"price": price})
		return
	}
	currentPrice := p.pricingEngine.Calculate(productID)
	p.memcached.SetPrice(productID, currentPrice)
	json.NewEncoder(w).Encode(map[string]interface{}{"price": currentPrice})
}

func (m *MetricsCollector) RecordMetric(ctx context.Context, metricName string, value float64) {
	key := fmt.Sprintf("metrics:%s:%d", metricName, time.Now().Unix())
	m.redis.HSet(ctx, key, "value", value)
	m.redis.Expire(ctx, key, 7*24*time.Hour)
}

