import (
	"github.com/streadway/amqp"
	"log"
)

func StartConsumer(url string) {
	conn, err := amqp.Dial(url)
	if err != nil {
		log.Fatal(err)
	}
	ch, _ := conn.Channel()
	q, _ := ch.QueueDeclare("tasks", true, false, false, false, nil)
	msg, _ := ch.Consume(q.Name, "", true, false, false, false, nil)
	for delivery := range msg {
		log.Printf("Task: %s", string(delivery.Body))
	}
}

