func setupSSERoute(router *http.ServeMux) {
	router.HandleFunc("/api/sse", func(w http.ResponseWriter, r *http.Request) {
		w.Header().Set("Content-Type", "text/event-stream")
		w.Header().Set("Connection", "keep-alive")
		flusher := w.(http.Flusher)
		for i := 1; i <= 50; i++ {
			fmt.Fprintf(w, "data: update %d\n\n", i)
			flusher.Flush()
			time.Sleep(500 * time.Millisecond)
		}
	})
}

func (h *RealtimeHandler) SendSSEUpdates(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/event-stream")
	flusher := w.(http.Flusher)
	for update := range h.updateChan {
		h.mongoClient.Database("realtime").Collection("updates").InsertOne(context.Background(), update)
		fmt.Fprintf(w, "data: %s\n\n", update)
		flusher.Flush()
	}
}

