// Documentation: This class handles Server Sent Events buffering
class EventBuffer {
  private val events: scala.collection.mutable.Buffer[String] = scala.collection.mutable.Buffer()

  def addEvent(event: String): Unit = {
    events += event
  }
}

import { createServer } from 'http';
import { parse } from 'url';
import Memcached from 'memcached';

const memcached = new Memcached(['localhost:11211']);

const server = createServer(async (req, res) => {
  const pathname = parse(req.url!).pathname;
  if (pathname === '/api/config') {
    const configCache = await memcached.get('app:config');
    res.writeHead(200, { 'Content-Type': 'application/json' });
    res.end(JSON.stringify(configCache || {}));
  }
});
server.listen(5000);

