import { EventEmitter } from 'events';

class SoapMessageHandler extends EventEmitter {
  constructor() {
    super();
    this.on('soap-message', (envelope: any) => {
      console.log('Processing incoming SOAP envelope:', envelope);
      this.handleRequest(envelope);
    });
  }

  private handleRequest(envelope: any): void {
    const body = envelope.Body;
    console.log('Extracted body:', body);
  }
}

export default SoapMessageHandler;

